#include <stdint.h>

//#if !defined(__SOFT_FP__) && defined(__ARM_FP)
//  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
//#endif

typedef unsigned int unit32_t;

int main(void)
{
  	unit32_t *pClkCtrlReg = (unit32_t*)0x40023830;
	unit32_t *pPortDModeReg = (unit32_t*)0x40020C00;
	unit32_t *pPortDOutReg = (unit32_t*)0x40020C14;

	// 1. enable the clock for GPIOD peripheral in the AHB1ENR
	*pClkCtrlReg |= (1 << 3);

	// 2. configure the mode of the IO pin as output
	// a. clear the 24th and 25th bit position (CLEAR)
	*pPortDModeReg &= ~(3 << 24);
	// b. make 24th bit position as 1 (SET)
	*pPortDModeReg |= (1 << 24);

	// 3. SET 12th bit of the output data register to make I/O pin-12 as HIGH
	*pPortDOutReg |= (1 << 12);



    /* Loop forever */
	for(;;);
}
